<?php

/**
 * ECSHOP 蓝海支付插件
 */

if (!defined('IN_ECS')) {
    die('Hacking attempt');
}

$payment_lang = ROOT_PATH . 'languages/' . $GLOBALS['_CFG']['lang'] . '/payment/blueoceanpay.php';

if (file_exists($payment_lang)) {
    global $_LANG;

    include_once $payment_lang;
}

/* 模块的基本信息 */
if (isset($set_modules) && $set_modules == true) {
    $i = isset($modules) ? count($modules) : 0;

    /* 代码 */
    $modules[$i]['code'] = basename(__FILE__, '.php');

    /* 描述对应的语言项 */
    $modules[$i]['desc'] = 'blueoceanpay_desc';

    /* 是否支持货到付款 */
    $modules[$i]['is_cod'] = '0';

    /* 是否支持在线支付 */
    $modules[$i]['is_online'] = '1';

    /* 作者 */
    $modules[$i]['author'] = 'BlueOceanPay TEAM';

    /* 网址 */
    $modules[$i]['website'] = 'http://www.blueoceanpay.com';

    /* 版本号 */
    $modules[$i]['version'] = '1.0.0';

    /* 配置信息 */
    $modules[$i]['config'] = array(
        array('name' => 'merchant_id', 'type' => 'text', 'value' => ''),
        array('name' => 'public_key', 'type' => 'text', 'value' => ''),
        array('name' => 'blueoceanpay_currency', 'type' => 'select', 'value' => ''),
    );

    return;
}

/**
 * 类
