    /**
     * 生成支付代码
     * @param   array   $order      订单信息
     * @param   array   $payment    支付方式信息
     */
    public function get_code($order, $payment)
    {
        global $_LANG;

        $url = 'http://api.hk.blueoceanpay.com/payment/pay';
        // 支付订单数据
        $requestData = array(
            'appid'        => $payment['merchant_id'],
            'payment'      => 'blueocean.qrcode',
            'body'         => $order['order_sn'],
            'out_trade_no' => $order['order_sn'] . '-' . $order['log_id'] . '-' . time(),
            'total_fee'    => $order['order_amount'] * 100,
            'notify_url'   => return_url(basename(__FILE__, '.php')),
        );

        $requestData['sign'] = $this->sign($requestData, $payment['public_key']);

        $result     = self::httpPost($url, json_encode($requestData));
        $returnData = json_decode($result, true);

        if ($returnData['code'] != 200) {
            $html = $returnData['code'] . ': ' . $returnData['message'];
        } else {
            $code_url = $returnData['data']['qrcode'];
            
            // 支付成功跳转地址，及服务器完整地址判断处理。
            if ($_SERVER['SERVER_PORT'] == 80) {
                $success_url = 'http://' . $_SERVER['SERVER_NAME'] . $_SERVER['REQUEST_URI'];
                $server_url  = 'http://' . $_SERVER['SERVER_NAME'];
            } elseif ($_SERVER['SERVER_PORT'] == 443) {
                $success_url = 'https://' . $_SERVER['SERVER_NAME'] . $_SERVER['REQUEST_URI'];
                $server_url  = 'https://' . $_SERVER['SERVER_NAME'];
            } else {
                $success_url = 'http://' . $_SERVER['SERVER_NAME'] . ':' . $_SERVER['SERVER_PORT'] . $_SERVER['REQUEST_URI'];
                $server_url  = 'http://' . $_SERVER['SERVER_NAME'] . ':' . $_SERVER['SERVER_PORT'];
            }

            // 生成支付二维码
            $html = '
                    <style type="text/css">
                    body { overflow:hidden; }
                    #overlay { display: none; background: #000; filter: alpha(opacity=80); opacity: 0.8; position: absolute; top: 0px; left: 0px; width: 100%; height: 100%; z-index: 9999999; }
                    #qr-area{ visibility:hidden; width:30%; height:70%; position:absolute; z-index: 99999999;color:#fff; letter-spacing:0.1rem; text-align:center; }
                    </style>
                    <div id="overlay"></div>
