    /**
     * 响应操作
     */
    public function respond()
    {
        if ($data = $_POST) {
            $payment    = $GLOBALS['db']->getRow("SELECT pay_config FROM " . $GLOBALS['ecs']->table('payment') . " WHERE pay_code = 'blueoceanpay'");
            $pay_config = unserialize($payment['pay_config']);
            $public_key = '';
            foreach ($pay_config as $value) {
                if ($value['name'] == 'public_key') {
                    $public_key = $value['value'];
                }
            }
            if (($data['sign'] != $this->sign($data, $public_key))) {
                echo 'Sign Error';
                exit;
            }
            $order_sn = explode('-', $data['out_trade_no']);
            /* 改变订单状态 */
            order_paid($order_sn[1]);
            return 1;
        } else {
            echo 'Blueocean Pay Error: ' . $data['message'];
        }
    }

    /**
     * 以post方式提交请求
     * @param string $url
     * @param array|string $data
     * @return bool|mixed
     */
    public static function httpPost($url, $data)
    {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, false);
        curl_setopt($ch, CURLOPT_POST, true);
        if (is_array($data)) {
            foreach ($data as &$value) {
                if (is_string($value) && stripos($value, '@') === 0 && class_exists('CURLFile', false)) {
                    $value = new CURLFile(realpath(trim($value, '@')));
                }
            }
        }
        curl_setopt($ch, CURLOPT_POSTFIELDS, $data);
        $data = curl_exec($ch);
        curl_close($ch);
        if ($data) {
            return $data;
        }
        return false;
    }

    /**
     * 数组数据签名
     * @param array $data 参数
     * @param string $key 密钥
     * @return string 签名
     */
    public function sign($data, $key)
    {
        $ignoreKeys = ['sign', 'key'];
        ksort($data);
        $signString = '';
        foreach ($data as $k => $v) {
            if (in_array($k, $ignoreKeys)) {
                unset($data[$k]);
                continue;
            }
            $signString .= "{$k}={$v}&";
        }
        $signString .= "key={$key}";
        return strtoupper(md5($signString));
    }

    /**
     * 生成随机字符串
     * @param $length
     * @return null|string
     */
    public function getRandChar($length)
    {
        $str    = null;
        $strPol = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789abcdefghijklmnopqrstuvwxyz";
        $max    = strlen($strPol) - 1;

        for ($i = 0; $i < $length; $i++) {
            $str .= $strPol[rand(0, $max)];
        }
        return $str;
    }
